#include "10B2.h"
char getline1(){
char *array;
  if(!(array=(int*)malloc(sizeof(int)*masize)))
  {
	  printf("not enough memory!/n");
	  exit (1);
	  if(fgets(array,100stdin)!=NULL)
	  {
		  return array;
	  }
}
int main(void)
{
    char *lines[100];
    char **linep = lines;
    char **endp = lines + 100;

    while (linep < endp) {
        *linep = getline1();
        if (*linep) {
            if (!**linep)
                printf("Error in getline1: Pointer to totally empty line!\n"), exit(1);
            linep++;
        }
        else
            break;
    }
    
    while (linep > lines) {
        linep--;
        printf("%s", *linep);
        free(*linep);
    }

    return 0;
}

