  if (!(integer = (int *) malloc(sizeof(int)*many))){
    printf("Not enough memory!\n");
    exit(1);
  }

  for(i=1;i<=many;i++){
    scanf("%d",integer);
    if(i!=many){
      integer++;
    }
  }

  for(i=0;i<many;i++){
    printf("%d\n",*integer);
    integer--;
  }

  free(integer);

  return 0;
}

#include <stdio.h>
#include <stdlib.h>


int *getline1(void)
{
	int *buff;
	if(!(buff=(int *) malloc(sizeof(int)*100)))
	{
		printf("Not enough memory!\n");
		exit(1);
	}
	
	if(fgets(buff,100,stdin)!=NULL)
	{
		return buff;
	}
	free(buff);
}

int main(void)
{
    int *linep,*endp;
	int i=0,j=100;


    while (linep < linep+100) {
        linep = getline1();
        if (linep) {
            if (!*linep)
                printf("Error in getline1: Pointer to totally empty line!\n"), exit(1);
            linep++;
			i++
        }
        else
            break;
    }
    endp = linep +i;
	while((linep-i+j)>=linep)
	{
		free(linep-i+j)
		j--;
	}
    while (linep > endp) {
        linep--;
        printf("%d", *linep);
        free(linep);
    }

    return 0;
}

