#include <stdio.h>

#ifndef SIZE
#define SIZE 5
#endif


typedef struct
{
	double data[SIZE][SIZE];
}Matrix;

Matrix input_matrix()
{
	Matrix a;
	int i,j;
	
	for(i = 0; i < SIZE; i++)
	{
		for(j = 0; j < SIZE; j++)
		{
			scanf("%lf", &a.data[i][j]);
		}
	}
	return a;
}

void output_matrix(Matrix a)
{
	int i,j;
	
	for(i = 0; i < SIZE; i++)
	{
		for(j = 0; j < SIZE; j++)
		{
			printf("%7.2lf",a.data[i][j]);
		}
		printf("\n");
	}
}

Matrix add_matrix(Matrix a, Matrix b)
{
	Matrix m;
	int i,j;
	
	for(i = 0; i < SIZE; i++)
	{
		for(j = 0; j < SIZE; j++)
		{
			m.data[i][j] = a.data[i][j] + b.data[i][j];
		}
	}
	return m;
}

Matrix subtract_matrix(Matrix a, Matrix b)
{
	Matrix m;
	int i,j;
	
	for(i = 0; i < SIZE; i++)
	{
		for(j = 0; j < SIZE; j++)
		{
			m.data[i][j] = a.data[i][j] - b.data[i][j];
		}
	}
	return m;
}

Matrix multiply_matrix(Matrix a, Matrix b)
{
	Matrix m;
	int i , j, k;
	double sum = 0;
	
	for(i = 0; i < SIZE; i++)
	{
		for(j = 0; j < SIZE; j++)
		{
			//m.data[i][j] = 0;
			sum = 0;
			for(k = 0; k < SIZE; k++)
			{
				sum += a.data[i][k] * b.data[k][j];
				m.data[i][j] = sum;
			}
			
			
		}
	}
	return m;
}


int main (void)
{
    Matrix a = input_matrix();
    Matrix b = input_matrix();
    Matrix r;

    printf("Matrix A:\n");
    output_matrix(a);
    printf("\nMatrix B:\n");
    output_matrix(b);

    r = add_matrix(a, b);
    printf("\nMatrices added:\n");
    output_matrix(r);

    printf("\nMatrices subtracted:\n");
    output_matrix(subtract_matrix(a, b));

    r = multiply_matrix(a, b);
    printf("\nMatrices multiplied:\n");
    output_matrix(r);

    return 0;
}

