#include <stdio.h>
#include <stdlib.h>

int main()
{
  int i,n,*num;
  
  if(!(num = (int *)malloc(sizeof(int)*10))){
    printf("Not enough memory!\n"),exit(1);
  }
  
  scanf("%d",&n);
  
  for(i=0;i<n;i++){
   scanf("%d",num+i);
  }
  
  for(i=n-1;i>=0;i--){
    printf("%d\n",*(num+i));
  }
    free(num);  
  return 0;
}

#include <stdio.h>
#include <stdlib.h>

int main(void)
{
	int num;
	int i;
    int *p;

	scanf("%d", &num);
	
	if(!(p = (int *)malloc(sizeof(int)*num)))
		printf("Not enough memory!\n"), exit(1);
	
	for(i = 0;i < num; i++)
	{
		scanf("%d", p);
		p++;
	}
	p--;
	for(i = num; i > 0; i--)
	{
		printf("%d\n", *p);
		free(p);
		p--;
	}
	
    return 0;
}

