#include <stdio.h>
#include <stdlib.h>

int main()
{   
    //領域確保
    int *p;
    int kazu,i,*first;
    scanf("%d",&kazu);
    if (!(p = (int *) malloc(sizeof(int)*kazu))){
        first = p;
        printf("Not enough memory!\n"), exit(1);
    }

    //ポインタのさす方向を最初scanfの時は順々に、printするときに後ろからポインタを指す
    for (i = 0; i < kazu; ++i)
    {
        if(scanf("%d",p) == EOF)
        {
            printf("error\n");
        }
        else{
            p++;
        }
    }
    while(p > first){
        p--;
        printf("%d\n",*p);
    }
    free(p);
    return 0;
   
}

