#include <stdio.h>
#include <stdlib.h>

int *getline1(void);
int main(void)
{
    int *lines[100];
    int **linep = lines;
    int **endp = lines + 100;
	int i=0;

    while (linep < endp) {
        *linep = getline1();
        if (*linep) {
            if (!**linep)
                printf("Error in getline1: Pointer to totally empty line!\n"), exit(1);
            linep++;
        }
        else
            break;
    }
    while (linep > lines) {
        linep--;
		if(i!=0){
			printf("%d\n", **linep);
		}
		i=i+1;
        free(*linep);
    }

    return 0;
}
int *getline1(void)
{ 
	int i=100;
	int *line;
	if(!(line=(int *)malloc(sizeof (int)*i))){
		printf("Not enough memory!\n");
		free(line);
		exit(1);
	}
	if(scanf("%d",line)==EOF){
		return 0;
	}
	return line;
}

#include <stdio.h>
#include <stdlib.h>
int main(void){
	int *work,*result;
	int n,i;
	scanf("%d",&n);
	work=malloc(sizeof(int)*n)；
	if(work==NULL){
		printf("出直しナ")；
		exit(1);
	}
	for(i=0;i<n;i++){
		scanf("%d",work++);
	}
	for(i=0;i<n;i++){
		printf("%d\n",*--work);
	}
	free(work);
	return 0;
}

