int main(void)
{
	int *work,*result;
	int n,i;
	scanf("%d",&n);
	work=malloc(sizeof(int)*n);
	if(work=NULL){
		printf("おととい来やがれ");
		exit(1);
	}
	for(i=0;i<n;i++){
		scanf("%d",work++);
	}
	for(i=0;i<n;i++){
		printf("%d\n",*--work);
	}
	free(work);
	return 0;
}

