#include <stdio.h>
#include <stdlib.h>

int main()
{
	int *buff;
	int line;
	int lines=1;
	
	scanf("%d",&line);//何行読み込むのか
	
	if(!(buff=malloc(sizeof(int)*line))){
		//printf("メモリが足りません");
		exit(1);
	}
	
	
	
	while(lines<=line){//読み込む行数まで繰り返す
		
		scanf("%d",&(*buff));//行読み込み
		buff++;//次の文字が入っている領域へ
		lines++;//次の行番号
	    
	}
	
	lines=lines-1;
	
	while(lines>0){
		
		buff--;
		printf("%d\n",*buff);
		lines--;
	}
	
	free(buff);
	
	return 0;
}

