#include <stdio.h>
#include <stdlib.h>

int main()
{
	int size, *number, *numberp, *end;
	scanf("%d", &size);
	
	if(!(number = (int*) malloc (sizeof(int)*size)))
	{
		printf("Not enough memory!\n");
		exit(1);
	}
	numberp = number;
	end = numberp + size;
	
	while(numberp < end)
	{
		printf("%p and %p\n", numberp, end);
		scanf("%d", numberp);
		numberp++;
	}
		printf("%p and %p\n", numberp, end);
	
	end -= size;
	
	while(numberp > end)
	{
		numberp--;
        printf("%d\n", *numberp);
		printf("%p and %p\n", numberp, end);
	}
	free(numberp);
	
	return 0;
}

