{
	int c;
	char *buf;
	int masize =100;
	char *my_array;
    if(!(my_array=(char *)malloc(sizeof(char)*masize))){
		printf("メモリ不足です"),exit(1);
	}
	if((c=getchar())!=EOF){
		fgets(buf,100,stdin);
		reverse();
	}
	if(c==EOF){
		free(my_array);
		return 0;
	}
	putchar(c);
    printf("%s",buf);
	free(my_array);
	return 0;
}
